import React from 'react'
import styled from 'styled-components'

import Me from '../images/blue_me.svg';

const CardList = styled.div`
    display:flex;
    position:absolute;
    top:60%;

    padding:3rem;
    overflow-x: scroll;
`

const Card = styled.article`
    @import url('https://fonts.googleapis.com/css2?family=DM+Mono:wght@300;400;500&display=swap');
    font-family: 'DM Mono', monospace;
    display: flex;
    position:relative;
    flex-direction:column;
    height:55vh;
    width: 400px;
    min-width:250px;
    padding:1.5rem;
    color:white;

    border-radius: 16px;
    background: #17141d;
    box-shadow: -1rem 0 3rem #000;

    transition :.2s;


    &:hover{
        transform: translateY(-2rem): 
    }

    &:hover~&{
        transform:translateX(130px);
    }

    &:not(first-child){
        margin-left: -130px;
    }
`

const CardHeader = styled.header`
    margin-bottom: auto;

    & p{font-size: 14px;
    margin: 0 0 1rem;
    color: #7a7a8c;
    }

    & h2 {
        font-size: 20px;
        margin: .25rem 0 auto;
        text-decoration: none;
        color: inherit;
        border: 0;
        display: inline-block;
        cursor: pointer;
    }

    & h2:hover {
        background: linear-gradient(90deg,#ff8a00,#e52e71);
        text-shadow: none;
        -webkit-text-fill-color: transparent;
        -webkit-background-clip: text;
        background-clip: text;
    }


`

const CardInfo =  styled.div`
margin: 3rem 0 0;
display: grid;
grid-template-columns: 75px 1fr;
align-items: center;
position: relative;

     & img{
        width: 40px;
        height: 40px;
        border-radius: 50%;
        filter: grayscale(100%);
        display: block;
        overflow: hidden;
        margin: 16px 10px;
     }
`

const Avatar = styled.div`
`
const AuthorName = styled.div`
`
const AutorNamePrefix = styled.div`
    font-style: normal;
    font-weight: 700;
    color: #7a7a8c;
`

const HalfCircle = styled.svg`
position: absolute;
bottom: 0;
left: 0;
width: 60px;
height: 48px;
fill: none;
stroke: #ff8a00;
stroke-width: 8;
stroke-linecap: round;
pointer-events: none;
`

function ExperienceComponent() {
    return (
        <CardList>
            <Card>
                <CardHeader>
                    <p>May 25th 2020</p>
                    <h2>Card tricks are fun, Hi mom!</h2>
                </CardHeader>
                <CardInfo>
                    <Avatar>
                        <img src={Me} />
                    </Avatar>
                    <HalfCircle  viewBox="0 0 106 57">
                        <path d="M102 4c0 27.1-21.9 49-49 49S4 31.1 4 4"></path>
                    </HalfCircle>

                    <AuthorName>
                        <AutorNamePrefix>
                            Author
                        </AutorNamePrefix>Jeff Delany
                    </AuthorName>
                </CardInfo>

            </Card>

            <Card>
                <CardHeader>
                    <p>May 25th 2020</p>
                    <h2>Card tricks are fun, Hi mom!</h2>
                </CardHeader>
                <CardInfo>
                    <Avatar>
                        <img src={Me} />
                    </Avatar>
                    <HalfCircle  viewBox="0 0 106 57">
                        <path d="M102 4c0 27.1-21.9 49-49 49S4 31.1 4 4"></path>
                    </HalfCircle>

                    <AuthorName>
                        <AutorNamePrefix>
                            Author
                        </AutorNamePrefix>Jeff Delany
                    </AuthorName>

                </CardInfo>
            </Card>

            <Card>
                <CardHeader>
                    <p>May 25th 2020</p>
                    <h2>Card tricks are fun, Hi mom!</h2>
                </CardHeader>
            </Card>

            <Card>
                <CardHeader>
                    <p>May 25th 2020</p>
                    <h2>Card tricks are fun, Hi mom!</h2>
                </CardHeader>
            </Card>

            <Card>
                <CardHeader>
                    <p>May 25th 2020</p>
                    <h2>Card tricks are fun, Hi mom!</h2>
                </CardHeader>
            </Card>

        </CardList>
    )
}

export default ExperienceComponent
